package week1.day1;

public class LearnIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		if (num % 2 == 0)
			System.out.println("Wonderful");
		else if(num%2!=0)
		System.out.println("Good");
		else
			System.out.println("Nice");
	}

}
